INSERT INTO `bnf_drugs` (`id`, `url`, `page`, `drug_name`, `indications_dosage`, `contra_indications`, `cautions`, `side_effects`, `pregnancy`, `breast_feeding`, `prescribing_info`, `patient_advice`, `directions`, `specific_info`) VALUES
                                                                                                                                                                                                                                       (1624, 'https://bnf.nice.org.uk/drug/zopiclone.html', 'zopiclone.html', 'ZOPICLONE', '[{\"populationType\":\"Adult\",\"text\":\"7.5&#8239;mg once daily for up to 4 weeks, dose to be taken at bedtime.\"},{\"populationType\":\"Elderly\",\"text\":\"Initially 3.75&#8239;mg once daily for up to 4 weeks, dose to be taken at bedtime, increased if necessary to 7.5&#8239;mg daily.\"},{\"populationType\":\"Adult\",\"text\":\"Initially 3.75&#8239;mg once daily for up to 4 weeks, dose to be taken at bedtime, increased if necessary to 7.5&#8239;mg daily.\"}]', '[\"Marked neuromuscular respiratory weakness\",\"myasthenia gravis\",\"respiratory failure\",\"severe sleep apnoea syndrome\"]', '[\"Avoid prolonged use (risk of tolerance and withdrawal symptoms)\",\"chronic pulmonary insufficiency (increased risk of respiratory depression)\",\"depression\",\"elderly\",\"history of alcohol abuse\",\"history of drug abuse\",\"muscle weakness\",\"psychiatric illness\"]', '{\"commonOrVeryCommon\":[\"Dry mouth\",\"taste bitter\"],\"rareOrVeryRare\":[\"Behaviour abnormal\",\"confusion\",\"dyspnoea\",\"fall\",\"hallucination\",\"irritability\",\"libido disorder\",\"memory impairment\",\"skin reactions\"],\"uncommon\":[\"Anxiety\",\"dizziness\",\"fatigue\",\"headache\",\"nausea\",\"sleep disorders\",\"vomiting\"],\"notKnown\":[\"Cognitive disorder\",\"concentration impaired\",\"delusions\",\"depressed mood\",\"diplopia\",\"drug dependence\",\"dyspepsia\",\"movement disorders\",\"muscle weakness\",\"paraesthesia\",\"respiratory depression\",\"speech disorder\",\"withdrawal syndrome\"]}', '               <section class=\"advice GeneralInformation\">                 <div>                               <p>Not recommended (risk of neonatal withdrawal symptoms).</p>                               <p>Use during late pregnancy or labour may cause neonatal hypothermia, hypotonia, and respiratory depression.</p>                             </div>               </section>           ', '                            <section class=\"advice GeneralInformation\">                 <div>                               <p>Present in milk&#8212;avoid.</p>                             </div>               </section>           ', '', '', '', ''),
                                                                                                                                                                                                                                       (1625, 'https://bnf.nice.org.uk/drug/zuclopenthixol.html', 'zuclopenthixol.html', 'ZUCLOPENTHIXOL', '[{\"populationType\":\"Adult\",\"text\":\"Initially 20&#8211;30&#8239;mg daily in divided doses, increased if necessary up to 150&#8239;mg daily; usual maintenance 20&#8211;50&#8239;mg daily (max. per dose 40&#8239;mg), for debilitated patients, use elderly dose.\"},{\"populationType\":\"Elderly\",\"text\":\"Initially 5&#8211;15&#8239;mg daily in divided doses, increased if necessary up to 150&#8239;mg daily; usual maintenance 20&#8211;50&#8239;mg daily (max. per dose 40&#8239;mg).\"}]', '[\"Apathetic states\",\"CNS depression\",\"comatose states\",\"phaeochromocytoma\",\"withdrawn states\"]', '[\"Hyperthyroidism\",\"hypothyroidism\"]', '{\"notKnown\":[\"Anxiety\",\"appetite abnormal\",\"asthenia\",\"concentration impaired\",\"confusion\",\"depression\",\"diarrhoea\",\"dyspnoea\",\"eye disorders\",\"fever\",\"flatulence\",\"gait abnormal\",\"gastrointestinal discomfort\",\"glucose tolerance impaired\",\"headaches\",\"hepatic disorders\",\"hot flush\",\"hyperacusia\",\"hyperglycaemia\",\"hyperhidrosis\",\"hyperlipidaemia\",\"hypersalivation\",\"hypothermia\",\"malaise\",\"memory loss\",\"muscle complaints\",\"nasal congestion\",\"nausea\",\"neuromuscular dysfunction\",\"pain\",\"palpitations\",\"paraesthesia\",\"photosensitivity reaction\",\"reflexes increased\",\"seborrhoea\",\"sexual dysfunction\",\"skin reactions\",\"sleep disorders\",\"speech disorder\",\"syncope\",\"thirst\",\"thrombocytopenia\",\"tinnitus\",\"urinary disorders\",\"vertigo\",\"vision disorders\",\"vulvovaginal dryness\",\"weight decreased\",\"withdrawal syndrome\"]}', '', '', '', '', '', ''),
                                                                                                                                                                                                                                       (1626, 'https://bnf.nice.org.uk/drug/zuclopenthixol-acetate.html', 'zuclopenthixol-acetate.html', 'ZUCLOPENTHIXOL ACETATE', '[{\"populationType\":\"Adult\",\"text\":\"50&#8211;150&#8239;mg, then 50&#8211;150&#8239;mg after 2&#8211;3&#8239;days if required, (1 additional dose may be needed 1&#8211;2 days after the first injection); maximum cumulative dose 400&#8239;mg in 2 weeks and maximum 4 injections; maximum duration of treatment 2 weeks&#8212;if maintenance treatment necessary change to an oral antipsychotic 2&#8211;3 days after last injection, or to a longer acting antipsychotic depot injection given concomitantly with last injection of zuclopenthixol acetate; to be administered into the gluteal muscle or lateral thigh.\"},{\"populationType\":\"Elderly\",\"text\":\"50&#8211;100&#8239;mg, then 50&#8211;100&#8239;mg after 2&#8211;3&#8239;days if required, (1 additional dose may be needed 1&#8211;2 days after the first injection); maximum cumulative dose 400&#8239;mg in 2 weeks and maximum 4 injections; maximum duration of treatment 2 weeks&#8212;if maintenance treatment necessary change to an oral antipsychotic 2&#8211;3 days after last injection, or to a longer acting antipsychotic depot injection given concomitantly with last injection of zuclopenthixol acetate; to be administered into the gluteal muscle or lateral thigh.\"}]', '[\"CNS depression\",\"comatose states\",\"phaeochromocytoma\"]', '[\"Hyperthyroidism\",\"hypothyroidism\"]', '{\"notKnown\":[\"Anxiety\",\"appetite abnormal\",\"asthenia\",\"concentration impaired\",\"confusion\",\"depression\",\"diarrhoea\",\"dyspnoea\",\"eye disorders\",\"fever\",\"flatulence\",\"gait abnormal\",\"gastrointestinal discomfort\",\"glucose tolerance impaired\",\"headaches\",\"hepatic disorders\",\"hot flush\",\"hyperacusia\",\"hyperglycaemia\",\"hyperhidrosis\",\"hyperlipidaemia\",\"hypersalivation\",\"hypothermia (<span class=\\\"sideEffectQualifier\\\">dose-related<\\/span>)\",\"malaise\",\"memory loss\",\"muscle complaints\",\"nasal congestion\",\"nausea\",\"neuromuscular dysfunction\",\"pain\",\"palpitations\",\"paraesthesia\",\"photosensitivity reaction\",\"reflexes increased\",\"seborrhoea\",\"sexual dysfunction\",\"skin reactions\",\"sleep disorders\",\"speech disorder\",\"syncope\",\"thirst\",\"thrombocytopenia\",\"tinnitus\",\"urinary disorders\",\"vertigo\",\"vision disorders\",\"vulvovaginal dryness\",\"weight decreased\",\"withdrawal syndrome\"]}', '', '', '           <section class=\"importantSafetyInformation\">               <section class=\"advice ImportantSafetyInformation\">                 <div>                               <p>When prescribing, dispensing, or administering, check that this is the correct preparation&#8212;this preparation is usually used in hospital for an <em>acute episode</em> and should <strong>not</strong> be confused with depot preparations which are usually used in the community or clinics for <em>maintenance</em> treatment.</p>                             </div>               </section>               <section class=\"advice ImportantSafetyInformation\">                 <h4>Safe Practice</h4>                 <div>                                                              <p>Zuclopenthixol acetate has been confused with zuclopenthixol decanoate; care must be taken to ensure the correct drug is prescribed and dispensed.</p>                             </div>               </section>           </section>           ', '', '', ''),
                                                                                                                                                                                                                                       (1627, 'https://bnf.nice.org.uk/drug/zuclopenthixol-decanoate.html', 'zuclopenthixol-decanoate.html', 'ZUCLOPENTHIXOL DECANOATE', '[{\"populationType\":\"Adult\",\"text\":\"Test dose 100&#8239;mg, dose to be administered into the upper outer buttock or lateral thigh, followed by 200&#8211;500&#8239;mg after at least 7&#8239;days, then 200&#8211;500&#8239;mg every 1&#8211;4&#8239;weeks, adjusted according to response, higher doses of more than 500mg can be used; do not exceed 600&#8239;mg weekly.\"},{\"populationType\":\"Elderly\",\"text\":\"A quarter to half usual starting dose to be used.\"}]', '[\"CNS depression\",\"comatose states\",\"phaeochromocytoma\"]', '[\"Hyperthyroidism\",\"hypothyroidism\",\"QT interval prolongation\",\"when transferring from oral to depot therapy, the dose by mouth should be reduced gradually\"]', '{\"notKnown\":[\"Anxiety\",\"appetite abnormal\",\"asthenia\",\"concentration impaired\",\"confusion\",\"depression\",\"diarrhoea\",\"dyspnoea\",\"eye disorders\",\"fever\",\"flatulence\",\"gait abnormal\",\"gastrointestinal discomfort\",\"glucose tolerance impaired\",\"headaches\",\"hepatic disorders\",\"hot flush\",\"hyperacusia\",\"hyperglycaemia\",\"hyperhidrosis\",\"hyperlipidaemia\",\"hypersalivation\",\"hypothermia\",\"malaise\",\"memory loss\",\"muscle complaints\",\"nasal congestion\",\"nausea\",\"neuromuscular dysfunction\",\"pain\",\"palpitations\",\"paraesthesia\",\"photosensitivity reaction\",\"reflexes increased\",\"seborrhoea\",\"sexual dysfunction\",\"skin reactions\",\"sleep disorders\",\"speech disorder\",\"syncope\",\"thirst\",\"thrombocytopenia\",\"tinnitus\",\"urinary disorders\",\"vertigo\",\"vision disorders\",\"vulvovaginal dryness\",\"weight decreased\",\"withdrawal syndrome\"]}', '', '', '           <section class=\"importantSafetyInformation\">               <section class=\"advice ImportantSafetyInformation\">                 <div>                               <p>When prescribing, dispensing, or administering, check that this is the correct preparation&#8212;this preparation is used for <em>maintenance</em> treatment and should <strong>not</strong> be used for the short-term management of an <em>acute episode</em>.</p>                             </div>               </section>               <section class=\"advice ImportantSafetyInformation\">                 <h4>Safe Practice</h4>                 <div>                                                              <p>Zuclopenthixol decanoate has been confused with zuclopenthixol acetate; care must be taken to ensure the correct drug is prescribed and dispensed.</p>                             </div>               </section>           </section>           ', '', '               <section class=\"advice DirectionForAdministration\">                 <div>                               <p>In general not more than 2&#8211;3&#8239;mL of oily injection should be administered at any one site.</p>                               <p>Correct injection technique (including use of z-track technique) and rotation of injection sites are essential.</p>                               <p>When initiating therapy with sustained-release preparations of conventional antipsychotics, patients should first be given a small test-dose as undesirable side-effects are prolonged.</p>                             </div>               </section>           ', '');
